class Solution {
    public void flatten(TreeNode root) {

        if (root == null) {
            return;
        }

        Stack&lt;TreeNode&gt; stk = new Stack&lt;&gt;();
        TreeNode trk = null;
        TreeNode curr = root;

        while (!stk.isEmpty()) {

            while (curr != null) {
                stk.push(curr);

                if (trk == null) {
                    trk = root;
                } else {
                    trk.left = null;
                    trk.right = curr;
                    trk = trk.right;
                }
                curr = curr.left;
            }

            curr = stk.pop();
            curr = curr.right;
        }

    }
}
