private boolean isConnected(City city, Set&lt;City&gt; checked) {
    if (this == city || connections.contains(city)) {
        return true;
    }
    checked.add(this);
    
    for (City nested : connections) {
        if (checked.contains(nested)) {
            continue;
        }
        return nested.isConnected(city, checked);
    }
    return false;
}
