    			System.exit(-1);
    		}
    
    		player.setCurrentRoom(currentRoom);
    		currentRoom.addPlayer(player);
    		player.addPropertyChangeListener(pce -&gt; pcSupport.firePropertyChange(GAME_MODEL, null, player));
    
    	}
    
