        &quot;is_optional&quot;: false,
        &quot;regexes&quot;: [
          &quot;.+&quot;
        ],
        &quot;paramType&quot;: &quot;PUBSUB_TOPIC&quot;
      },
      {
        &quot;name&quot;: &quot;outputTableSpec&quot;,
        &quot;label&quot;: &quot;Output BigQuery table&quot;,
        &quot;helpText&quot;: &quot;Output BigQuery table. For example, &lt;project&gt;:&lt;dataset&gt;.&lt;table_name&gt;. Mandatory when sinkType is BIGQUERY.&quot;,
        &quot;isOptional&quot;: false,
