    		/* 0009: Op 6201 0200       sget-object v1, ?@2 */
    		v1 = java.lang.System.out; // (Field@2)
    		/* 000b: Op 6000 0100       sget v0, ?@1 */
    		v0 = Parent.parent_int; // (Field@1)
    		/* 000d: Op d800 0001       add-int/lit8 v0, 1 */
    		v0 +=  1;
