    }
    
    public static void reverseNodes(LinkedListNode head, LinkedListNode a1, LinkedListNode a2) {
        
        if(a2.next == null) {
            a2.next = a1;
            head = a2;
        }
        else {
            LinkedListNode nextNode = a2.next;
            reverseNodes(head, a2, nextNode);
            a2.next = a1;
        }
        
                
    }
