                .asFloatBuffer()
                .put(fboTexCoords);
        fboVertexBuffer.position(0);
        mFboVertexBufferId = vbos[2];
        GLES30.glBindBuffer(GLES30.GL_ARRAY_BUFFER, mFboVertexBufferId);
        GLES30.glBufferData(GLES30.GL_ARRAY_BUFFER, fboTexCoords.length * 4, fboVertexBuffer, GLES30.GL_STATIC_DRAW);

        GLES30.glBindBuffer(GLES30.GL_ARRAY_BUFFER, 0);
    }

    public SurfaceTexture getSurfaceTexture() {
        return mSurfaceTexture;
    }

    public void setUnityTexture(int texId) {
        mFrameTextureId = texId;
    }

    public void createSurfaceTexture(SurfaceTexture.OnFrameAvailableListener callback) {
        if (mOESTextureId == -1) {
            mOESTextureId = createTextureOES();
        }

        if(mFBOId == -1) {
            mFBOId = createFramebufferObject();
        }

        mSurfaceTexture = new SurfaceTexture(mOESTextureId);
