    public int createTextureOES() {
        int[] textures = new int[1];
        // GL_TEXTURE_EXTERNAL_OES
        GLES30.glGenTextures(1, textures, 0);
        mOESTextureId = textures[0];
        GLES30.glBindTexture(GLES11Ext.GL_TEXTURE_EXTERNAL_OES, mOESTextureId);
        GLES30.glTexParameterf(GLES11Ext.GL_TEXTURE_EXTERNAL_OES, GLES30.GL_TEXTURE_MIN_FILTER, GLES30.GL_LINEAR);
        GLES30.glTexParameterf(GLES11Ext.GL_TEXTURE_EXTERNAL_OES, GLES30.GL_TEXTURE_MAG_FILTER, GLES30.GL_LINEAR);
        GLES30.glTexParameteri(GLES11Ext.GL_TEXTURE_EXTERNAL_OES, GLES30.GL_TEXTURE_WRAP_S, GLES30.GL_REPEAT);
        GLES30.glTexParameteri(GLES11Ext.GL_TEXTURE_EXTERNAL_OES, GLES30.GL_TEXTURE_WRAP_T, GLES30.GL_REPEAT);

        return mOESTextureId;
    }

    public int createTexture2D() {
        int[] textures = new int[1];
        // GL_TEXTURE_2D
        GLES30.glGenTextures(1, textures, 0);
        mFrameTextureId = textures[0];

        return mFrameTextureId;
    }

    public int createFramebufferObject() {
        // FBO
        int[] fbos = new int[1];
        GLES30.glGenFramebuffers(1, fbos, 0);
        mFBOId = fbos[0];
        int status = GLES30.glCheckFramebufferStatus(GLES30.GL_FRAMEBUFFER);
        if(status != GLES30.GL_FRAMEBUFFER_COMPLETE) {
            Log.d(&quot;FBO&quot;, &quot;FBO not ready! &quot; + status);
        }
        return mFBOId;
    }

    public void destroy() {
        if(mOESTextureId != -1) {
            int[] textures = new int[1];
            textures[0] = mOESTextureId;
            GLES20.glDeleteTextures(1, textures, 0);
            mOESTextureId = -1;
        }
    }

    public void unload() {
        if (mSurface != null) {
            mSurface.release();
            mSurface = null;
        }

        if (mSurfaceTexture != null) {
            mSurfaceTexture.release();
            mSurfaceTexture = null;
        }

