import java.util.Optional;
import javax.swing.*;
import static java.lang.Math.cos;
import static java.lang.Math.PI;
import static java.lang.Math.sin;

public class GraphicsDemo {

    final static int TICK_DELAY = 10;

    static class DemoPanel extends JPanel {

        private Optional&lt;Instant&gt; startInstant = Optional.empty();

        public DemoPanel() {
            super(true);
            setPreferredSize(new Dimension(1200, 800));
            setBackground(Color.BLUE);
        }

        void updateState() {
            // ...
            repaint();
        }

        @Override
        protected void paintComponent(Graphics g) {
            super.paintComponent(g);
            Instant now = Instant.now();

            if (!startInstant.isPresent()) {
                startInstant = Optional.of(now);
            }
            double centisElapsed
                    = Duration.between(startInstant.get(), now).toMillis() / 10.0;

            int x = 1200 + (int) (centisElapsed * sin(centisElapsed * PI / 720) / 5);
            int y =  800 + (int) (centisElapsed * cos(centisElapsed * PI / 720) / 5);

            // Copy the Graphics object because we&#39;re going to persistently modify it
            Graphics2D g2D = (Graphics2D) g.create();

            g2D.scale(0.5, 0.5);
            g2D.setColor(Color.BLACK);
            g2D.fillRect(x, y, 200, 200);
            g2D.dispose();

            // This was important:
            getToolkit().sync();
        }
    }

    // This method should be run on the EDT
    public static void startGUI() {
        JFrame mainWindow = new JFrame(&quot;GraphicsDemo&quot;);

        DemoPanel panel = new DemoPanel();
        // javax.swing.Timer: fires events on the EDT
        Timer animationTimer = new Timer(TICK_DELAY, ae -&gt; panel.updateState());

        mainWindow.setDefaultCloseOperation(WindowConstants.EXIT_ON_CLOSE);
