        int pixelSize = minDimension / maxPixelBlocks; //no va a ser
        // Asegurarse de que el tama&#241;o del p&#237;xel sea al menos un valor m&#237;nimo. (en el caso que sea 1).
        pixelSize = Math.max(pixelSize, 10);
        return pixelSize;
    }

