            } catch (IOException e) {
                e.printStackTrace();
            }

            getResult.set(answerHTTP);

            handler.post(() -&gt; {
                //UI Thread work here
                if(getResult.get() != null &amp;&amp; !getResult.get().equals(&quot;&quot;)) {
                    mainActivity.process_your_result_in_main(getResult.get());
                }
            });
        });
