        Cursor cursor = db.query(table_name, columns, null, null, null, null, null);
        
        if (cursor != null &amp;&amp; cursor.moveToFirst()) {
            do {
                names.add(cursor.getString(cursor.getColumnIndex(column_name)));
            } while (cursor.moveToNext());
            
            cursor.close();
        }
        
        return names;
    }

