                    for (Cell cell : row) {
                        headers.add(cell.getStringCellValue());
                    }
                }
    
                List&lt;DataRow&gt; contents = new ArrayList&lt;&gt;();
                while (rows.hasNext()) {
                    Row row = rows.next();
