List&lt;Integer&gt; list, even, odd;
even = new ArrayList&lt;&gt;();
odd = new ArrayList&lt;&gt;();
list = new ArrayList&lt;&gt;();
for (String string : scanner.nextLine().split(&quot; +&quot;))
    list.add(Integer.parseInt(string));
for (int a = 0, b; a &lt; list.size() - 1; a++)
