public static String reverse(String str) {
    char[] arr = str.toCharArray();

    for (int i = 0, j = str.length() - 1; i &lt; j; i++, j--) {
        char tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }

    return new String(arr).intern();
}
