			&lt;id&gt;generate-model&lt;/id&gt;
			&lt;configuration&gt;
				&lt;target&gt;
					&lt;taskdef resource=&quot;net/sf/antcontrib/antlib.xml&quot;/&gt;
					&lt;delete dir=&quot;${project.basedir}/src/main/java/com/mycompany/myproject/model&quot; /&gt;
					&lt;for param=&quot;dtd&quot;&gt;
						&lt;path&gt;
							&lt;fileset dir=&quot;${project.basedir}/src/main/resources/schema/path1&quot;&gt;
