The [Row Number Table](http://tips4java.wordpress.com/2008/11/18/row-number-table/) bases the row header on the number of rows in the JTable (most current version will be found at this link).

    import java.awt.*;
    import java.awt.event.*;
    import java.beans.*;
    import javax.swing.*;
    import javax.swing.event.*;
    import javax.swing.table.*;
    
    /*
     *  Use a JTable as a renderer for row numbers of a given main table.
     *  This table must be added to the row header of the scrollpane that
     *  contains the main table.
     */
    public class RowNumberTable extends JTable
        implements ChangeListener, PropertyChangeListener
    {
        private JTable main;
    
        public RowNumberTable(JTable table)
        {
            main = table;
            main.addPropertyChangeListener( this );
            main.getModel().addTableModelListener( this );
    
            setFocusable( false );
            setAutoCreateColumnsFromModel( false );
            setSelectionModel( main.getSelectionModel() );
            setRowHeight( main.getRowHeight() );
    
            TableColumn column = new TableColumn();
            column.setHeaderValue(&quot; &quot;);
            addColumn( column );
            column.setCellRenderer(new RowNumberRenderer());
    
            getColumnModel().getColumn(0).setPreferredWidth(50);
            setPreferredScrollableViewportSize(getPreferredSize());
        }
    
        @Override
        public void addNotify()
        {
            super.addNotify();
    
            Component c = getParent();
    
            //  Keep scrolling of the row table in sync with the main table.
    
            if (c instanceof JViewport)
            {
                JViewport viewport = (JViewport)c;
                viewport.addChangeListener( this );
            }
        }
    
        /*
         *  Delegate method to main table
         */
        @Override
        public int getRowCount()
        {
            return main.getRowCount();
    	}
    
        @Override
        public int getRowHeight(int row)
        {
            int rowHeight = super.getRowHeight(row);
            int mainRowHeight = main.getRowHeight(row);
    
            if (rowHeight != mainRowHeight)
            {
                super.setRowHeight(row, mainRowHeight);
            }
    
            return mainRowHeight;
        }
    
        /*
         *  No model is being used for this table so just use the row number
         *  as the value of the cell.
         */
        @Override
        public Object getValueAt(int row, int column)
        {
            return Integer.toString(row + 1);
        }
    
        /*
         *  Don&#39;t edit data in the main TableModel by mistake
         */
        @Override
        public boolean isCellEditable(int row, int column)
        {
            return false;
        }
    
        /*
         *  Do nothing since the table ignores the model
         */
        @Override
        public void setValueAt(Object value, int row, int column) {}
    //
    //  Implement the ChangeListener
    //
        public void stateChanged(ChangeEvent e)
        {
            //  Keep the scrolling of the row table in sync with main table
    
            JViewport viewport = (JViewport) e.getSource();
            JScrollPane scrollPane = (JScrollPane)viewport.getParent();
            scrollPane.getVerticalScrollBar().setValue(viewport.getViewPosition().y);
        }
    //
    //  Implement the PropertyChangeListener
    //
        public void propertyChange(PropertyChangeEvent e)
        {
            //  Keep the row table in sync with the main table
    
            if (&quot;selectionModel&quot;.equals(e.getPropertyName()))
            {
                setSelectionModel( main.getSelectionModel() );
            }
    
            if (&quot;rowHeight&quot;.equals(e.getPropertyName()))
            {
                repaint();
            }
    
            if (&quot;model&quot;.equals(e.getPropertyName()))
            {
                main.getModel().addTableModelListener( this );
                revalidate();
            }
        }
    
        /*
         *  Attempt to mimic the table header renderer
         */
        private static class RowNumberRenderer extends DefaultTableCellRenderer
        {
            public RowNumberRenderer()
            {
                setHorizontalAlignment(JLabel.CENTER);
            }
    
            public Component getTableCellRendererComponent(
                JTable table, Object value, boolean isSelected, boolean hasFocus, int row, int column)
            {
                if (table != null)
                {
                    JTableHeader header = table.getTableHeader();
    
                    if (header != null)
                    {
                        setForeground(header.getForeground());
                        setBackground(header.getBackground());
                        setFont(header.getFont());
                    }
                }
    
                if (isSelected)
                {
                    setFont( getFont().deriveFont(Font.BOLD) );
                }
    
                setText((value == null) ? &quot;&quot; : value.toString());
                setBorder(UIManager.getBorder(&quot;TableHeader.cellBorder&quot;));
    
                return this;
            }
        }
    }

