    import com.google.gson.*;
    import java.lang.reflect.Type;
    import java.util.Map;

    class GsonStringToBytesExample {
        // POJO
        static class SomeName {
          public byte[] message;
        }

        // custom deserializer capable of converting json string field to pojo&#39;s byte[] field
        static class SomeNameDeserializer implements JsonDeserializer&lt;SomeName&gt; {
          private final static Gson gson = new Gson();

          @Override
          public SomeName deserialize(JsonElement json, Type typeOfT, JsonDeserializationContext context) throws JsonParseException {
            // reading json fields to map
            Map&lt;String, JsonElement&gt; fieldsMap = json.getAsJsonObject().asMap();
            // reading json string message
            String messageStr = fieldsMap.get(&quot;message&quot;).getAsString();
            // creating bytes array from string
            byte[] messageBytes = messageStr.getBytes();
            // creating GSON&#39;s JsonElement from bytes array
            JsonElement dataBytesJsonElement = gson.toJsonTree(messageBytes);
            // substituting json &quot;message&quot; field with bytes array object
            fieldsMap.put(&quot;message&quot;, dataBytesJsonElement);
            // finally deserializing to POJO
            return gson.fromJson(json, typeOfT);
        }
      }

      public static void main(String[] args) {
        String json = &quot;&quot;&quot;
                {&quot;message&quot;:&quot;hi&quot;}
                &quot;&quot;&quot;;

        Gson gson = new GsonBuilder()
                // registering custom deserializer
                .registerTypeAdapter(SomeName.class, new SomeNameDeserializer())
                .create();

        SomeName someName = gson.fromJson(json, SomeName.class);
        // here we have pojo with bytes array from string
        System.out.println(new String(someName.message));
      }
    }

