            jsonSchema.append(&quot;      \&quot;properties\&quot;: {\n&quot;);

            for (DescriptorProtos.FieldDescriptorProto field : message.getFieldList()) {
                jsonSchema.append(&quot;        \&quot;&quot; + field.getName() + &quot;\&quot;: {\n&quot;);
                jsonSchema.append(&quot;          \&quot;type\&quot;: \&quot;&quot; + fieldTypeToJsonSchemaType(field.getType()) + &quot;\&quot;\n&quot;);
