    final String json = &quot;{\&quot;objects\&quot; : [\&quot;One\&quot;, \&quot;Two\&quot;, \&quot;Three\&quot;]}&quot;;

    final JsonNode arrNode = new ObjectMapper().readTree(json).get(&quot;objects&quot;);
    if (arrNode.isArray()) {
        for (final JsonNode objNode : arrNode) {
            System.out.println(objNode);
        }
    }

