class Solution {
  public int countNegatives(int[][] grid) {
    final int m = grid.length;
    final int n = grid[0].length;
    int ans = 0;
    int i = m - 1;
    int j = 0;

    while (i &gt;= 0 &amp;&amp; j &lt; n) {
      if (grid[i][j] &lt; 0) {
        ans += n - j;
        --i;
      } else {
        ++j;
      }
    }

