        String choice = &quot;&quot;;
        int finalI = keys.length + 1; // Will hold the last item number in Menu for &#39;Quit&#39;:
        
        // Keep looping while &#39;choice&#39; is empty:
        while (choice.isEmpty()) {
            // Prompt string:
            System.out.println(&quot;Select the Key Name you would like the Value for:&quot;);
            
            // Loop through the &#39;keys[]&#39; array and build into menu:            
            for (int i = 0; i &lt; keys.length; i++) {
                System.out.println(&quot;  &quot; + (i + 1) + &quot;) &quot; + keys[i]);
            }
            // Menu separator so to apply the &#39;Quit&#39; menu item beneath it:
            System.out.println(&quot;  -------------------------&quot;);
            System.out.println(&quot;  &quot; + finalI + &quot;) Quit This Application!&quot;);
            System.out.print(&quot;Your Choice? -&gt; &quot;);
            
            /* Get User Input and trim it so to remove any possible
               leading or trailing whitespaces:      */
            choice = userInput.nextLine().trim();
            
            // Entry Validation:
            /* Make sure the entry is an integer numerical value. If it
               is then make sure the numercal value entered is within 
               the range of the number of menu item (however number of 
               menu items there actually are):            */
            if (!choice.matches(&quot;\\d+&quot;) || Integer.parseInt(choice) &lt; 1 || 
                                           Integer.parseInt(choice) &gt; finalI) {
                // If input validation fails then inform User and repropmt:
                System.out.println(&quot;Invalid Menu Choice! (&quot; + choice + &quot;) Try Again...&quot; + LS);
                choice = &quot;&quot;; // Empty &#39;choice&#39; to ensure reprompt:
            }
        }
        
        /* If we make it to this point then validation was successful 
           so convert the menu item number to an int type variable. 
           This value will be used as an index number (minus 1) to get 
           the related Key Name from the &#39;keys[]&#39; string array:   */
        int choiceVal = Integer.parseInt(choice);
        
        // Has QUIT been selected (the last menu item)?
        if (choiceVal == finalI) {
            System.out.println(LS + &quot;Quitting - Bye Bye&quot; + LS);
            // Effectively exit this method which will ultimately end the application:
            return; 
        }
        
        /* No...Quit hasn&#39;t been selected so let&#39;s get and 
           display the selected Key Value:        */
        Object keyValue = getJsonKeyValue(json, keys[choiceVal - 1]);
        System.out.println(LS + &quot;Value for the Key: \&quot;&quot; + keys[choiceVal - 1] 
                           + &quot;\&quot; is: -&gt; &quot; + keyValue.toString() + LS);
        pressEnterToContinue();
    }
    
    /* A prompt to allow User to continue and disply the menu again.
       (a million and a half ways to do this sort of thing)    */
    private void pressEnterToContinue() {
        // Prompt...
        String entry = &quot;&quot;;
        while (entry.isEmpty()) {
            System.out.println(&quot;&lt;&lt; Press ENTER To Continue &gt;&gt;&quot;);
            System.out.println(&quot;&lt;&lt;     Enter q to Quit     &gt;&gt;&quot;);
            entry = userInput.nextLine().trim();
            // Validate...
            /* If the entry isn&#39;t empty (which an ENTER Key Hit 
               will do) and the entry wasn&#39;t a &#39;Q&#39; in any letter 
               case then the entry is Invalid to continue the demo 
               so, ask again.            */
            if (!entry.isEmpty() &amp;&amp; !entry.equalsIgnoreCase(&quot;q&quot;)) {
                System.out.println(&quot;Invalid Entry! (&quot; + entry + &quot;) - Try Again...&quot; + LS);
                entry = &quot;&quot;; // Empty so to ensure re-loop.
                continue;
            }
            
            // If we make it to this point then, validation is successful:
            if (!entry.equalsIgnoreCase(&quot;q&quot;)) {
                // Make &#39;entry&#39; contain something relative so not to re-loop:
                entry = &quot;continueON&quot;;
            }
            // Otherwise....QUIT.
            else {
                System.out.println(&quot;Quitting - Bye Bye&quot; + LS);
                System.exit(0);
            }
           
        }
        
        /* Call the startApp() method again but with no arguments
           so to display the menu again:        */
        startApp();
    }

    /**
     * This method retrieves the Key Names within a JSON String and returns them
     * within a String[] array.&lt;br&gt;
     *
     * @param jsonString (String) The JSON String to work on.&lt;br&gt;
     * 
     * @param parentNodeName (Optional - String VarArg) A lot of times Keys and 
     * their respective values are nested within sever specific parent nodes. 
     * This parameter allows you to specify the parent node from where to acquire 
     * the desired Key names.
     * &lt;p&gt;
     * If there are parent nodes within the JSON string and you supply nothing 
     * to this parameter then, the outer most Parent Key names will be returned.
     *&lt;p&gt;
     * @return (String[] Array) A String array of Key names contained within the
     *         supplied JSON String.
     */
    public String[] getJsonKeys(String jsonString, String... parentNodeName) {
        String parentNode = &quot;&quot;;
        if (parentNodeName.length &gt; 0 &amp;&amp; !parentNodeName[0].trim().isEmpty()) {
               parentNode = parentNodeName[0];
        }
        
        org.json.JSONObject obj = new org.json.JSONObject(jsonString);
        if (!parentNode.isEmpty()) {
            org.json.JSONObject parentObj = obj.getJSONObject(parentNode);
            java.util.Set&lt;String&gt; keyNames = parentObj.keySet();
            return keyNames.toArray(new String[0]);
        }
        else {
            java.util.Set&lt;String&gt; keyNames = obj.keySet();
            return keyNames.toArray(new String[0]);
        }
    }

    /**
     * This method retrieves the Key Value for the supplied Key Name contained
     * within the supplied JSON String.
     * 
     * @param jsonString (String) The JSON String to work on.&lt;br&gt;
     * 
     * @param keyName (String) The Key Name to get the related Value for.&lt;br&gt;
     * 
     * @return (Object) The Value related to the supplied Key Name. You will 
     * need to cast the returned object to the desired data type the key value 
     * is meant to be or use the &#39;toString()&#39; method.
     */
    public Object getJsonKeyValue(String jsonString, String keyName) {
        org.json.JSONObject obj = new org.json.JSONObject(jsonString);
        Object keyValue = obj.get(keyName);
        return keyValue;
    }
    // DONE.
}
