    public static void print(List&lt;String&gt; one, String oneName,
                             List&lt;String&gt; two, String twoName,
                             List&lt;String&gt; three, String threeName) {
        printMissing(one, oneName, two, twoName, three, threeName);
        printMissing(two, twoName, one, oneName, three, threeName);
        printMissing(three, threeName, one, oneName, two, twoName);

        printNew(one, oneName, two, twoName, three, threeName);
        printNew(two, twoName, one, oneName, three, threeName);
        printNew(three, threeName, one, oneName, two, twoName);

        printCommon(three, threeName, one, oneName, two, twoName);
    }

    private static void printMissing(List&lt;String&gt; one, String oneName,
                                     List&lt;String&gt; two, String twoName,
                                     List&lt;String&gt; three, String threeName) {
        print(&quot;Missing&quot;, oneName, twoName, threeName, () -&gt; {
            List&lt;String&gt; res = new ArrayList&lt;&gt;(two);
            res.addAll(three);
            res.removeAll(one);
            return res;
        });
    }

    private static void printNew(List&lt;String&gt; one, String oneName,
                                 List&lt;String&gt; two, String twoName,
                                 List&lt;String&gt; three, String threeName) {
        print(&quot;New&quot;, oneName, twoName, threeName, () -&gt; {
            List&lt;String&gt; res = new ArrayList&lt;&gt;(one);
            res.removeAll(two);
            res.removeAll(three);
            return res;
        });
    }

    private static void printCommon(List&lt;String&gt; one, String oneName,
                                    List&lt;String&gt; two, String twoName,
                                    List&lt;String&gt; three, String threeName) {
        print(&quot;Common&quot;, oneName, twoName, threeName, () -&gt; {
            List&lt;String&gt; res = new ArrayList&lt;&gt;(one);
            res.retainAll(two);
            res.retainAll(three);
            return res;
        });
    }

    private static void print(String action,
                              String oneName,
                              String twoName,
                              String threeName,
                              Supplier&lt;List&lt;String&gt;&gt; result) {
        List&lt;String&gt; res = result.get();

        if (!res.isEmpty()) {
            System.out.format(&quot;%s in %s vs %s and %s: %s\n&quot;,
                              action, oneName, twoName, threeName, res);
        }
    }
