		    } );
		System.out.printf( &quot;No: %d, OK: %d, Error: %d, Error.inc(38): %d, Error.inc(4, 4): %d%n&quot;,
		    CountOf.NO.(), CountOf.OK.(), CountOf.ERROR.(), CountOf.ERROR.( 38 ), CountOf.ERROR.( 4, 4 ) );

		// Output:
		// No: 7, OK: 3, Error: 4, Error.inc(38): 42, Error.inc(4, 4): 50
	}
}

