```
@Configuration
public class CamelCustomConfiguration {

    @Bean
    public SchedulerFactoryBean customSchedulerFactory(
            DataSource dataSource, QuartzProperties quartzProperties) {
        Properties properties = new Properties();
        properties.putAll(quartzProperties.getProperties());

        SchedulerFactoryBean factory = new SchedulerFactoryBean();
        factory.setOverwriteExistingJobs(true);
        factory.setWaitForJobsToCompleteOnShutdown(true);
        factory.setDataSource(dataSource);
        factory.setQuartzProperties(properties);
        factory.setJobFactory(new SpringBeanJobFactory());
        return factory;
    }

    @Bean
    public Scheduler customScheduler(
            DataSource dataSource, QuartzProperties quartzProperties) {
        return customSchedulerFactory(dataSource, quartzProperties).getScheduler();
    }

    @Bean
    public QuartzComponent customquartz(
            DataSource dataSource,
            QuartzProperties quartzProperties) {
        QuartzComponent component = new QuartzComponent();
        component.setScheduler(customScheduler(dataSource, quartzProperties));
        return component;
    }
}
