public void onNewIntent(Intent intent) {
    super.onNewIntent(intent);
    setIntent(intent);
    handleSharedTextIfNeeded(intent);
}

   @Override
   protected void onResume() {
       super.onResume();     

       ReactInstanceManager reactInstanceManager = getReactInstanceManager();
       reactInstanceManager.addReactInstanceEventListener(new ReactInstanceManager.ReactInstanceEventListener() {
           @Override
           public void onReactContextInitialized(ReactContext reactContext) {
               isReactContextInitialized = true;
               Intent intent = getIntent();
               handleSharedTextIfNeeded(intent);
      
               // Clear the intent to avoid processing it again
               setIntent(new Intent());
           }
       });
   }
    
   private void handleSharedTextIfNeeded(Intent intent) {
       String action = intent.getAction();
       String type = intent.getType();

       if (Intent.ACTION_SEND.equals(action) &amp;&amp; &quot;text/plain&quot;.equals(type)) {
           String sharedText = intent.getStringExtra(Intent.EXTRA_TEXT);
           if (sharedText != null) {
                           
               if (isReactContextInitialized) {
                   sendSharedTextToReactNative(sharedText);
                   handleSharedText(sharedText);
               } else {
                   // ReactContext is not initialized yet, handle it when initialized
                   handler.post(() -&gt; {
                       sendSharedTextToReactNative(sharedText);
                       handleSharedText(sharedText);
                   });
               }
           }
       }
   }

   private void sendSharedTextToReactNative(String sharedText) {

       ReactInstanceManager reactInstanceManager = getReactInstanceManager();
       ReactContext reactContext = reactInstanceManager.getCurrentReactContext();

       if (reactContext != null) {
            reactContext
           .getJSModule(DeviceEventManagerModule.RCTDeviceEventEmitter.class)
                   .emit(&quot;onSharedTextReceived&quot;, sharedText);
       } else {
           // Handle the case where the ReactContext is not available
       }
   }

   private void handleSharedText(String sharedText) {
       // Add your logic here to process the shared text in native code
   }
}


