  private static double deadband(double value, double deadband) {
    if (Math.abs(value) &gt; deadband) {
      if (value &gt; 0.0) {
        return (value - deadband) / (1.0 - deadband);
      } else {
        return (value + deadband) / (1.0 - deadband);
      }
    } else {
      return 0.0;
    }
  }

  private static double modifyAxis(double value) {
