            List&lt;Map&lt;?, ?&gt;&gt; maps = new ArrayList&lt;Map&lt;?, ?&gt;&gt;() {
                private static final long serialVersionUID = 1L;
                {
                    add(new LinkedHashMap&lt;String, Integer&gt;() {
