class RecordAdapterFactory : JsonAdapter&lt;Record&gt;() {
  @FromJson
  override fun fromJson(reader: JsonReader): Record {
    reader.beginObject()
    var attributes = Attributes(&quot;&quot;, &quot;&quot;)
    val recordBody = mutableMapOf&lt;String, String&gt;()
