        else -&gt; recordBody[nextName] = reader.nextString()
      }
    }
    reader.endObject()
    return Record(attributes, recordBody)
  }

  @ToJson
  override fun toJson(writer: JsonWriter, record: Record?) {
    writer.beginObject()
    writer.name(&quot;attributes&quot;)
    writer.beginObject()
    writer.name(&quot;type&quot;).value(record?.attributes?.type)
