    private static Person filter(Person person, Set&lt;Long&gt; ids) {
        if (person == null) {
            return null;
        }
        if (shouldDiscard(person, ids)) {
            return null;
        }
        person.setChildren(
            person.getChildren().stream()
            .map(child -&gt; filter(child, ids))
