                    lastVisibleItem = layoutManager.findLastVisibleItemPosition();
                    visibleItemCount = layoutManager.getChildCount();


                    if (totalItemCount == visibleItemCount ||
                            (totalItemCount &lt;= (lastVisibleItem + threshold) &amp;&amp;
                                    dy &gt; 0 &amp;&amp;
                                    !viewModel.isLoading()
                            ) &amp;&amp;
                                    viewModel.getResultsGamesLiveData().getValue() != null &amp;&amp;
                                    viewModel.getCurrentResults() != viewModel.getTotalResults()
                    ) {
                        MutableLiveData&lt;Result&gt; gamesLiveData = viewModel.getResultsGamesLiveData();

                        if (gamesLiveData.getValue() != null &amp;&amp;
                                gamesLiveData.getValue().isSuccess()) {

                            viewModel.setLoading(true);
                            gamesList.add(null);
                            gamesRecyclerViewAdapter.notifyItemRangeInserted(gamesList.size(),
                                    gamesList.size() + 1);


                            int page = viewModel.getOffset() + 1;
                            Log.e(TAG, String.valueOf(page));
                            viewModel.setOffset(page);
                            viewModel.resultGames(query);
                        }
                    }
                }
            }
        });

    }
