        private String normalize(String[] split, int maxLen){
    		StringBuilder sb = new StringBuilder(&quot;&quot;);
		    for(String s : split) {
		        for(int i = 0; i&lt;maxLen-s.length(); i++) sb.append(&#39;0&#39;);
