                    return true;
                } else {
                    if (minorUserVersion &lt;= minorLatestVersion) {
                        if (minorUserVersion &lt; minorLatestVersion) {
                            return true;
                        } else {
                            return patchUserVersion &lt; patchLatestVersion;
                        }
                    }
                }
            }
        } catch (Exception ignored) {
