public String formatTimeAgo(long millis) {
        String[] ids = new String[]{&quot;second&quot;,&quot;minute&quot;,&quot;hour&quot;,&quot;day&quot;,&quot;month&quot;,&quot;year&quot;};

        long seconds = millis / 1000;
        long minutes = seconds / 60;
        long hours = minutes / 60;
        long days = hours / 24;
