        int i = 1;
        final Scanner sc = new Scanner(scl.nextLine());
        while(sc.hasNext())
        {
            x = sc.next();

            System.out.println(x);
            if(StdRandom.bernoulli(1/i))
            {
                winner = x;
            }
            i++;
        }
        sc.close();
        scl.close();
        System.out.println(winner);
    }
}
