public class DataHandler {
    private static DataHandler handler = null;
    private static HikariDataSource dataSource = null;
    private static Statement stmt = null;
    private boolean connected = false;

    private DataHandler() {
        dbConnection(&quot;localhost:3306&quot;);

        if (getDataSource() == null) {
            connected = false;
            return;
        }
    }

    private void dbConnection(String ipPort) {
        String dbUrl = &quot;jdbc:mysql://&quot; + ipPort + &quot;/database?serverTimezone=UTC&quot;;

        HikariConfig config = new HikariConfig();

        config.setJdbcUrl(dbUrl);
        config.setUsername(&quot;user&quot;);
        config.setPassword(&quot;password&quot;);
        config.addDataSourceProperty(&quot;cachePrepStmts&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;prepStmtCacheSize&quot;, &quot;250&quot;);
        config.addDataSourceProperty(&quot;prepStmtCacheSqlLimit&quot;, &quot;2048&quot;);
        config.addDataSourceProperty(&quot;useServerPrepStmts&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;useLocalSessionState&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;rewriteBatchedStatements&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;cacheResultSetMetadata&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;elideSetAutoCommits&quot;, &quot;true&quot;);
        config.addDataSourceProperty(&quot;maintainTimeStats&quot;, &quot;false&quot;);
        config.setMaximumPoolSize(30);
        config.setAutoCommit(true);

        try {
            dataSource = new HikariDataSource(config);
            connected = true;
        } catch (HikariPool.PoolInitializationException e) {
            connected = false;
        }
    }

    public static DataHandler getInstance() {
        if (handler == null) handler = new DataHandler();

        return handler;
    }

    public static HikariDataSource getDataSource() {
        return dataSource;
    }

    public Connection getConnection() {
        try {
            return getDataSource().getConnection();
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return null;
    }

    public boolean isConnected() {
        return connected;
    }

    // I had to add Connection conn = getConnection() in every try-catch to get a connection
    // from the connection pool
    // and the try-catch block automatically closes the connection once its done
    public void loadAdherents() {
        try (Connection conn = getConnection()) {
            adherents.clear();

            String sql = &quot;SELECT * FROM ADHERENTS ORDER BY nom_prenom&quot;;
            ResultSet rs = conn.createStatement().executeQuery(sql);

            while (rs.next()) {
                /* FETCHING DATA */
            }

            rs.close();
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
    }
}
```

Before every call to the database, I added `try (Connection conn = getConnection())` to use a connection from the connection pool.

