    /**
     * In Spring this will read a file from src/main/resources/properties folder
     * @param fileName
     * @return
     * @throws Exception
     */
    public static String readFileInProperties(String fileName) throws Exception
    {
    	String retData = null;
    	try {
    	    // Get all the files under this inner resource folder: properties
    	    String scannedPackage = &quot;properties/&quot; + fileName;;
    	    PathMatchingResourcePatternResolver scanner = new PathMatchingResourcePatternResolver();
    	    Resource[] resources = scanner.getResources(scannedPackage);
    
    	    if (resources == null || resources.length == 0)
    	    {
    	        log.warn(&quot;Warning: could not find any resources in this scanned package: &quot; + scannedPackage);
    	    }
    	    else 
    	    {
    	    	if(resources.length != 1)
    	    	{
    	    		log.error(&quot;More than one file : &quot; + scannedPackage);
    	    		throw new RuntimeException(&quot;More than one file : &quot; + scannedPackage);
    	    	}
    	        for (Resource resource : resources) 
    	        {
    	            log.info(resource.getFilename());
    	            try(InputStream is = resource.getInputStream())
    	            {
    	                ByteArrayOutputStream buffer = new ByteArrayOutputStream();
    	                int nRead;
    	                byte[] data = new byte[1024];
    	                while ((nRead = is.read(data, 0, data.length)) != -1) 
    	                {
    	                    buffer.write(data, 0, nRead);
    	                }
    
    	                buffer.flush();
    	                byte[] byteArray = buffer.toByteArray();
