package com.cargill.api.casc.service.jarvis.repository;

import com.cargill.api.casc.service.jarvis.util.JarvisUtil;
import jakarta.persistence.EntityManager;
import jakarta.persistence.Query;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

//@Primary
@Component(&quot;jarvisrepo&quot;)
public class JarvisRepo {

	@Autowired
	private JarvisUtil jarvisUtil;

	@Autowired
	@Qualifier(value = &quot;mainEntityManager&quot;)
	private EntityManager em;

	@Value(&quot;#{${jarvis.whereClause.map}}&quot;)
	Map&lt;String, String&gt; whereClauseMap;

	@Value(&quot;#{${jarvis.table.map}}&quot;)
	Map&lt;String, String&gt; tableMap;

	@Value(&quot;#{${jarvis.query.map}}&quot;)
	Map&lt;String, String&gt; queryMap;

	@Value(&quot;#{${jarvis.orderBy.map}}&quot;)
	Map&lt;String, String&gt; orderByMap;

	@Value(&quot;#{${jarvis.groupBy.map}}&quot;)
	Map&lt;String, String&gt; groupByMap;

	@Value(&quot;#{${jarvis.custom.map}}&quot;)
	Map&lt;String, String&gt; customMap;

	private String getLimitOffsetQueryString() {
		return &quot; limit :limit offset :offset&quot;;
	}

	public &lt;T&gt; List&lt;T&gt; getQueryResponse(Map&lt;String, Object&gt; paramMap, Integer limit, Integer offset, String baseTableName) {

		StringBuilder selectQuery = new StringBuilder();
		Map&lt;String, Object&gt; queryParamMap = new HashMap&lt;&gt;();
		StringBuilder whereQueryString = new StringBuilder();
		selectQuery.append(queryMap.get(baseTableName));
		whereQueryString = jarvisUtil.addWhereCaluse(paramMap, queryParamMap, whereQueryString, whereClauseMap);
		if(customMap.containsKey(baseTableName)) {
			whereQueryString.append(customMap.get(baseTableName));
		}
		if(groupByMap.containsKey(baseTableName)){
			whereQueryString.append(groupByMap.get(baseTableName));
		}
		whereQueryString.append(orderByMap.get(baseTableName)).append(getLimitOffsetQueryString());
		if (whereQueryString.length() &gt; 0) {
			selectQuery.append(&quot; WHERE &quot;);
			selectQuery.append(whereQueryString);
		}
		Query query = em.createNativeQuery(selectQuery.toString(), baseTableName);
		addParamForOffsetLimit(queryParamMap, offset, limit);
		queryParamMap.forEach((key, value) -&gt; {
			query.setParameter(key, value);
		});
		return query.getResultList();
	}

	public List&lt;Object[]&gt; getTableCount(Map&lt;String, Object&gt; paramMap,String table) {
		StringBuilder routeRateCount = new StringBuilder();
		Map&lt;String, Object&gt; queryParamMap = new HashMap&lt;&gt;();
		StringBuilder whereQueryString = new StringBuilder();
		routeRateCount.append(&quot;select Count(*)  from &quot;).append(tableMap.get(table));
		whereQueryString = jarvisUtil.addWhereCaluse(paramMap, queryParamMap, whereQueryString, whereClauseMap);
		if (whereQueryString.length() &gt; 0) {
			routeRateCount.append(&quot; WHERE &quot;);
			routeRateCount.append(whereQueryString);
		}
		if(customMap.containsKey(table)) {
			routeRateCount.append(customMap.get(table));
		}
		Query query = em.createNativeQuery(routeRateCount.toString());
		queryParamMap.forEach((key, value) -&gt; {
			query.setParameter(key, value);
		});
		return query.getResultList();
	}


	private Map&lt;String, Object&gt; addParamForOffsetLimit(Map&lt;String, Object&gt; map, Integer offset, Integer limit) {
		map.put(&quot;limit&quot;, limit);
		map.put(&quot;offset&quot;, offset);
		return map;
	}


}


