        this.authService = authService;
    }  
  
    @Bean(&quot;myCustomWebClient&quot;)
    WebClient webClient() {
        return WebClient.builder()
                .filter(ExchangeFilterFunction.ofRequestProcessor(
                    (ClientRequest request) -&gt; {
                        Mono&lt;String&gt; tokenMono =  authService.getToken();
                        return tokenMono
                                  .map(token -&gt; ClientRequest.from(request).header(&quot;Access-Token&quot;, token).build())
                    }
                ))
                .build();
    }
}
```

