    		String nextVal = scan.next();
    		
            if (c.equals(nextVal) &amp;&amp; d.equals(nextVal) &amp;&amp; e.equals(nextVal)) {
            } else {
                temp = Integer.parseInt(i);
                if (temp &gt; highscore) {
