@Service
public class CartJobManager {
    public static final Logger             LOG                = LoggerFactory.getLogger(CartJobManager.class);

    private final Map&lt;JobKey, CartCronJob&gt; myKeyedJobs        = new HashMap&lt;&gt;();

    private final Set&lt;JobKey&gt;              myDisabledJobs     = new HashSet&lt;&gt;();
    private final Map&lt;JobKey, String&gt;      myJobCronSchedules = new HashMap&lt;&gt;();

    @Autowired
    private Set&lt;CartCronJob&gt;               myRegisteredJobs;

    @Autowired
    private SchedulerFactoryBean           mySchedulerFactory;

    @PostConstruct
    private void initJobManager() {
        LOG.info(&quot;Intialising catalog job manager&quot;);
        if (myRegisteredJobs == null || myRegisteredJobs.isEmpty()) {
            LOG.info(&quot;No job registered with us, nothing to manage here&quot;);
            return;
        }

        for (CartCronJob job : myRegisteredJobs) {
            if (myKeyedJobs.put(job.getJobKey(), job) != null) {
                throw new RuntimeException(&quot;More than one catalog jobs registered with key: &quot; + job.getJobKey());
            }
        }
    }

    public void unscheduleJob(JobKey jobKey) {
        Scheduler quartzScheduler = mySchedulerFactory.getScheduler();
        TriggerKey triggerKey = new TriggerKey(jobKey.getName());

        try {
            CronTrigger trigger = (CronTrigger) quartzScheduler.getTrigger(triggerKey);
            if (trigger == null) {
                LOG.info(&quot;No trigger already scheduled for job: {}&quot;, jobKey);
                return;
            }

            if (quartzScheduler.unscheduleJob(triggerKey)) {
                LOG.info(&quot;Successfully unscheduled an existing trigger for job: {}&quot;, jobKey);
            }
        } catch (SchedulerException e) {
            LOG.warn(&quot;Failed to unschedule an existing trigger for job: &quot; + jobKey, e);
        }
    }

    public void scheduleJob(JobKey jobKey, String cronExpr, Map&lt;String, String&gt; jobDataMap) {
        CartCronJob job = myKeyedJobs.get(jobKey);
        if (job == null) {
            LOG.warn(&quot;No job registered with key: {}, ignoring scheduling request&quot;, jobKey);
            return;
        }

        unscheduleJob(jobKey);

        try {
            LOG.info(&quot;Scheduling job: {} with cron schedule: {}&quot;, jobKey, cronExpr);
            JobDetail quartzJob = JobBuilder.newJob(job.getClass()).withIdentity(jobKey.getName()).build();
            if (null != jobDataMap) {
                quartzJob.getJobDataMap().putAll(jobDataMap);
            }
            CronTrigger trigger = TriggerBuilder.newTrigger().withIdentity(new TriggerKey(jobKey.getName())).withSchedule(CronScheduleBuilder.cronSchedule(cronExpr)).build();
            mySchedulerFactory.getScheduler().scheduleJob(quartzJob, trigger);
        } catch (Exception e) {
            LOG.warn(&quot;Failed to schedule the job: {} with cron schedule: {}&quot;, job.getJobKey(), cronExpr);
        }
    }

    public void updateJobConfig(CartJobConfig jobConfig) {
        JobKey jobKey = jobConfig.getJobKey();
        boolean isEnabled = jobConfig.isEnabled();
        String cronStr = jobConfig.getCronSchedule();
        Map&lt;String, String&gt; jobDataMap = getJobDataMapFromJson(jobConfig.getJobContext());

        if (myDisabledJobs.contains(jobKey) &amp;&amp; isEnabled) {
            myDisabledJobs.remove(jobKey);
            myJobCronSchedules.put(jobKey, cronStr);
            LOG.info(&quot;Enabling job: {}&quot;, jobKey);
            scheduleJob(jobKey, cronStr, jobDataMap);
            return;
        }

        if (!isEnabled &amp;&amp; myDisabledJobs.add(jobKey)) {
            LOG.info(&quot;Disabling job: {}&quot;, jobKey);
            unscheduleJob(jobKey);
            return;
        }

        String oldCronStr = myJobCronSchedules.put(jobKey, cronStr);
        if (!cronStr.equals(oldCronStr)) {
            LOG.info(&quot;Cron schedule changed for job: {} to: {} from: {}&quot;, oldCronStr, jobKey, cronStr);
            scheduleJob(jobKey, cronStr, jobDataMap);
        }
    }

    public String getCronSchedule(CartJobKeyEnum jobkey) {
        return myJobCronSchedules.get(jobkey.getJobKey());

    }

    private Map&lt;String, String&gt; getJobDataMapFromJson(String jobContextJson) {
        Map&lt;String, String&gt; map = null;
        try {
            if (!StringUtils.hasText(jobContextJson)) {
                return null;
            }
            map = new ObjectMapper().readValue(jobContextJson, new TypeReference&lt;Map&lt;String, String&gt;&gt;() {
            });
        } catch (Exception ex) {
            LOG.error(&quot;Failed to parse json {}&quot;, jobContextJson, ex);
        }
        return map;
    }
}

