    public static double calculateDiscountedPaybackPeriod(double initialInvestment, double discountRate, List&lt;Double&gt; cashFlows) {
        cashFlows.add(0, -initialInvestment);
        List&lt;Double&gt; PV = new ArrayList&lt;&gt;();    //Present Value
        List&lt;Double&gt; DCF = new ArrayList&lt;&gt;();   //Discounted Cash Flow
        List&lt;Double&gt; CCF = new ArrayList&lt;&gt;();   //Cumulative Discounted Cash Flow
        CCF.add(0, -initialInvestment);
        int x = 0;    //x is the last time period where CCF was negative
        double y = 0; //y is the absolute value of the CCF at the end of period X
        for (int j = 0; j &lt; cashFlows.size(); j++) {
            PV.add(j, 1 / Math.pow((1 + discountRate), j));
            DCF.add(j, cashFlows.get(j) * PV.get(j));
            CCF.add(j, DCF.stream().mapToDouble(Double::doubleValue).sum());
            if (CCF.get(j) &gt; 0) {
                x = j - 1;
                y = Math.abs(CCF.get(x));
            }
        }
        double z = DCF.get(x + 1);  //z is the value of the DCF in the next period after x
        return x + y / z;     //The Discounted Payback Period (DPP) is x + y/z
    }

