        inputInt = Integer.parseInt(scnr.nextLine());

        while ( !inputString.equals(&quot;Stop&quot;)){
            if ( inputInt &lt;= 35) {
                System.out.println( inputString + &quot;: reorder soon&quot;);
            }
            inputString = scnr.nextLine();
            inputInt = Integer.parseInt(scnr.nextLine());
        }
    }
}
