        &lt;exclusions&gt;
            &lt;exclusion&gt;
                &lt;groupId&gt;software.amazon.awssdk&lt;/groupId&gt;
                &lt;artifactId&gt;netty-nio-client&lt;/artifactId&gt;
            &lt;/exclusion&gt;
            &lt;exclusion&gt;
                &lt;groupId&gt;software.amazon.awssdk&lt;/groupId&gt;
                &lt;artifactId&gt;apache-client&lt;/artifactId&gt;
            &lt;/exclusion&gt;
