                        String location =  response.header(&quot;Location&quot;);
                        Log.d(WTBConstant.TAG, &quot;newLoc: &quot;+ location);
                        Request.Builder nwRequest = new Request.Builder().url(location).post(originalBody);
                        Request request = nwRequest.build();
                        return chain.proceed(request);

                    }
                    return response;
