        super.onDraw(canvas);
        int bottom = getHeight() - halfMargin;
        canvas.drawText(text, halfMargin, bottom, WHITE_BORDER_PAINT);
        canvas.drawText(text, halfMargin, bottom, BROWN_BORDER_PAINT);
