        }
    
        private TreeSet&lt;Integer&gt; generateIndicesToRemove(int count, int containerSize) {
            TreeSet&lt;Integer&gt; data = new TreeSet&lt;&gt;();
            ThreadLocalRandom.current().ints(0, containerSize)
                    .distinct()
