    import java.util.Objects;
    
    
    public class DraggableRecords extends Application {
    
        public static final class Category implements Serializable {
            private final String name;
            private final String description;
    
            public Category(String name, String description) {
                this.name = name;
                this.description = description;
            }
    
            public String getName() {
                return name;
            }
    
            public String getDescription() {
                return description;
            }
    
            @Override
            public boolean equals(Object o) {
                if (this == o) return true;
                if (o == null || getClass() != o.getClass()) return false;
                Category category = (Category) o;
                return Objects.equals(name, category.name) &amp;&amp; Objects.equals(description, category.description);
            }
    
            @Override
            public int hashCode() {
                return Objects.hash(name, description);
            }
    
            @Override
            public String toString() {
                return &quot;Category{&quot; +
                        &quot;name=&#39;&quot; + name + &#39;\&#39;&#39; +
                        &quot;, description=&#39;&quot; + description + &#39;\&#39;&#39; +
                        &#39;}&#39;;
            }
        }
    
        private static final ObservableList&lt;Category&gt; categories = FXCollections.observableArrayList(
