public class UserService {
    private static final Map&lt;Long, User&gt; userLookup;

    static {
        // Add a default user as ID=1
        User user = new User();
        user.setId(1L);
        user.setName(&quot;John Doe&quot;);
        Set&lt;Course&gt; courseSet = new HashSet&lt;&gt;();
        courseSet.add(new Course(&quot;CMSC100&quot;, &quot;Bits and Bytes of Computer and Information Sciences&quot;));
        courseSet.add(new Course(&quot;CMSC106&quot;, &quot;Introduction to C Programming&quot;));
        courseSet.add(new Course(&quot;CMSC133&quot;, &quot;Object Oriented Programming I Beyond Fundamentals&quot;));
        courseSet.add(new Course(&quot;CMSC250&quot;, &quot;Discrete Structures&quot;));
        user.setCourses(courseSet);
        userLookup = Collections.singletonMap(1L, user);
    }

    public static void main(String[] args) {
        UserService userService = new UserService();
        try {
            UserDTO dto = userService.getUserById(1L);
            System.out.println(dto);
        } catch (UserNotFoundException e) {
            e.printStackTrace();
        }
    }

    private UserRepository userRepository = new UserRepositoryImpl();
    private UserMapper userMapper = new UserMapper();

    public UserDTO getUserById(Long id) throws UserNotFoundException {
        return userRepository.findById(id).map(user -&gt; {
            UserDTO userDTO = userMapper.mapUserDTO(user);
            return userDTO;
        }).orElseThrow(() -&gt; new UserNotFoundException(String.format(&quot;User with id %s not found&quot;, id)));
    }

    private static class UserMapper {
        public UserDTO mapUserDTO(User user) {
            if (user == null) {
                return null;
            }
            UserDTO dto = new UserDTO();
            dto.setName(user.getName());
            // Sort when mapping...
            dto.setCourseList(user.getCourses().stream()
                    .sorted(Comparator.comparing(Course::getName))
                    .collect(Collectors.toList()));
            return dto;
        }
    }

    private static class UserRepositoryImpl implements UserRepository {
        @Override
        public Optional&lt;User&gt; findById(Long id) {
            return Optional.ofNullable(userLookup.get(id));
        }
    }

    private static class UserDTO {
        private String name;
        private List&lt;Course&gt; courseList;

        public String getName() {
            return name;
        }

        public void setName(String name) {
            this.name = name;
        }

        public List&lt;Course&gt; getCourseList() {
            return courseList;
        }

        public void setCourseList(List&lt;Course&gt; courseList) {
            this.courseList = courseList;
        }

        @Override
        public String toString() {
            return String.format(&quot;[User={name=%s,courseList=%s}]&quot;, name, courseList);
        }
    }

    private static class Course {
        private String id;
        private String name;

        public Course(String id, String name) {
            this.id = id;
            this.name = name;
        }

        public String getId() {
            return id;
        }

        public void setId(String id) {
            this.id = id;
        }

        public String getName() {
            return name;
        }

        public void setName(String name) {
            this.name = name;
        }

        @Override
        public String toString() {
            return String.format(&quot;[Course={id=%s,name=%s}]&quot;, id, name);
        }
    }

    private static class User {
        private long id;
        private String name;
        private Set&lt;Course&gt; courses;

        public long getId() {
            return id;
        }

        public void setId(long id) {
            this.id = id;
        }

        public String getName() {
            return name;
        }

        public void setName(String name) {
            this.name = name;
        }

        public Set&lt;Course&gt; getCourses() {
            return courses;
        }

        public void setCourses(Set&lt;Course&gt; courses) {
            this.courses = courses;
        }
    }

    private static interface UserRepository extends Repository&lt;User, Long&gt; {
        // See: UserRepositoryImpl
    }

    private static class UserNotFoundException extends Exception {
        public UserNotFoundException(String message) {
            super(message);
        }
    }

    private static interface Repository&lt;ENTITY, ID_TYPE&gt; {
        Optional&lt;ENTITY&gt; findById(ID_TYPE id);
    }
}
